import { useState } from "react";
import CardList from "./CardList";
import {Box, Typography, Modal, TextField, Button} from "@mui/material";

const addNewTaskBarStyle = {
    padding: "20px",
    background: "#fefffe",
    width: { xs: "300px", md: "500px" },
    borderRadius: "10px",
    margin: "20px 0px",
    fontSize: "25px",
    color: "#7b7b7b",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)",
};

const wrapperTodoListStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: "20px",
};

const wrapperHeaderStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
};

const headerTextStyle = {
    color: "#fefffb",
    fontSize: { xs:"50px", md:"60px"},
};

const headerTodoListLengthStyle = {
    padding: "20px 30px",
    backgroundColor: "#b0a3f5",
    boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)",
    fontSize: "50px",
    borderRadius: "10px",
    color: "white",
    fontWeight: "bold",
}

const modalStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%,-50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "solid 3px #b0a3f5",
    boxShadow: 24,
    p: 4,
    borderRadius: "10px",

}
const horizontalStyle = {
    width: "90%",
    backgroundColor: "white",
    boxShadow: "0 4px 8px 0 rgba(0,0,0,0.2)",
}

function Todo() {
    const [open, setOpen] = useState(false);
    const [todo, setTodo] = useState([]);

    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const [todoInput, setTodoInput] = useState("");
    const [todoError, setTodoError] = useState(true);

    function handleInput(e) {
        setTodoInput(e.target.vaule);
        if(e.target.vaule === null || e.target.vaule === ""){
            setTodoError(true);
        }
        setTodoError(false);
    }


    function handleAdd() {
        setTodo([...todo, "new task"]);
    }
    return(
        <>
        <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby = "modal-modal-title"
            aria-describedby = "mdal-modal-describedby"
        >
            <Box sx={modalStyle}>
                <TextField
                    error = {todoError}
                    id = "todo"
                    label = "What to you want to do ?"
                    variant="outlined"
                    onChange={(e) => handleInput(e)}
                />

                <Button
                    variant="contained"
                    onClick={handleAdd}
                    sx={{ margin: "20px 0px 0px 0px"}}
                    disabled={todoError}
                >
                    Add
                </Button>

            </Box>

        </Modal>

        <Box sx={{margin: "20px"}}>
            <div style={wrapperHeaderStyle}>
                <div>
                    <Typography variant="h1" sx={headerTextStyle}>
                        Incoming
                    </Typography>
                </div>

                <div style={headerTodoListLengthStyle}> 
                    {todo.length}
                </div>

            </div>

            <Box sx={wrapperTodoListStyle}>

                <Box sx={addNewTaskBarStyle} onClick={handleOpen}>

                + Add New Tasks

                </Box>

                {todo.map((item) =>{
                    return <CardList task={item}/>
                })}

            </Box>
            <hr style={horizontalStyle} />
        </Box></>
    );
}

export default Todo;
